create table customer(customerid[int] not null identity(1,1) primary key, customername[varchar](40) not null, customerprovince[int] not null, customerarea[int] not null, customercity[int] not null, customertown[int], customeraddr[varchar](100) not null default '', createdate[date] not null default getdate(), del_falg[char] not null default 'N')
create table customercontact(customercontactid[int] not null identity(1,1) primary key, customerid[int] not null foreign key (customerid) references customer(customerid), contactpersonname[varchar](10) not null, sex[char] not null default 'M', agegroup[char] not null default '2', liqueur[char] not null default 'Y', smoke[char] not null default 'N', sing[char] not null default 'N', gifts[char] not null default 'N', dwell[varchar](50) not null default '', del_falg[char] not null default 'N')
create table customercontactnumbers(customercontactnumberid[int] not null identity(1,1) primary key, customercontactid[int] not null foreign key (customercontactid) references customercontact(customercontactid), customercontactnumber[varchar](20) not null, del_falg[char] not null default 'N')
create table customercontactrelation(customerrelationid[int] not null identity(1,1) primary key, customercontactid[int] foreign key (customercontactid) references customercontact(customercontactid), relationcontent[varchar](200) not null default '', relationdate[date] not null default getdate(), del_falg[char] not null default 'N')