create table Customer(CustomerId[int] NOT NULL identity(1,1) primary key, CustomerName[varchar](40) NOT NULL, CustomerProvince[int] NOT NULL, CustomerArea[int] NOT NULL, CustomerCity[int] NOT NULL, CustomerTown[int], CustomerAddr[varchar](100) NOT NULL default '', CreateDate[date] NOT NULL default getdate(), Del_Falg[char] NOT NULL default 'N')
create table CustomerNumber(CustomerNumberId[int] NOT NULL identity(1,1) primary key, CustomerId[int] NOT NULL foreign key (CustomerId) references Customer(CustomerId), CustomerPhoneNumber[varchar](20) NOT NULL, Del_Falg[char] NOT NULL default 'N')
create table CustomerContact(CustomerContactId[int] NOT NULL identity(1,1) primary key, CustomerId[int] NOT NULL foreign key (CustomerId) references Customer(CustomerId), ContactPersonName[varchar](10) NOT NULL, Sex[char] NOT NULL default 'M', AgeGroup[char] NOT NULL default 'P', LikeLiqueur[char] NOT NULL default 'Y', Dwell[varchar](50) NOT NULL default '', Del_Falg[char] NOT NULL default 'N')
create table CustomerContactNumber(CustomerContactNumberId[int] NOT NULL identity(1,1) primary key, CustomerContactId[int] NOT NULL foreign key (CustomerContactId) references CustomerContact(CustomerContactId), CustomerContactPhoneNumber[varchar](20) NOT NULL, Del_Falg[char] NOT NULL default 'N')
create table CustomerContactRelation(CustomerRelationId[int] NOT NULL identity(1,1) primary key, CustomerContactId[int] foreign key (CustomerContactId) references CustomerContact(CustomerContactId), RelationContent[varchar](200) NOT NULL default '', RelationDate[date] NOT NULL default getdate(), Del_Falg[char] NOT NULL default 'N')